public class User {
    // ... fields

    public static class Builder {
        private String name;
        private String email;
        private String password;
        // ... other fields

        public Builder name(String name) {
            this.name = name;
            return this;
        }

        public Builder email(String email) {
            this.email = email;
            return this;
        }

        // ... other setters

        public User build() {
            User user = new User();
            user.setName(this.name);
            user.setEmail(this.email);
            // ... set other fields
            return user;
        }
    }
}

